import React, { Component } from "react";
import { Query, Mutation } from "react-apollo";
import ReactModal from "react-modal";
import gql from 'graphql-tag';


ReactModal.setAppElement("#root");

const updateQuoteQuery = gql`
mutation updateQuote(
  $id: String!,
  $quote: String!
) {
  updateQuote(
    input: {
      id: $id,
      quote: $quote
    }
  ) {
    id
    quote
  }
}
`;

const getAll = gql`
  query {
    quotes {
      id
      quote
    }
  }
`;

class EditQuote extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showEditQuote: this.props.isOpen,
      quote: this.props.editQuote
    };
    this.handleCloseEditQuote = this.handleCloseEditQuote.bind(this);
  }

  handleCloseEditQuote() {
    this.setState({ showEditQuote: false, quote: null });
    this.props.handleClose();
  }

  render() {
    let body;
    return (
      <div>
        {/*Edit Employee Modal - NOT DONE YET */}
        <ReactModal
          name="editQuote"
          isOpen={this.state.showEditQuote}
          contentLabel="Edit Quote"
        >
          <Mutation 
            mutation={updateQuoteQuery}
            update={(cache, { data: { updateQuote } }) => {
              const { quotes } = cache.readQuery({
                query: getAll
              });
              cache.writeQuery({
                query: getAll,
                data: {
                  quotes: quotes.filter(
                    q => q.id !== this.state.quote.id
                  )
                }
              });
            }}
          >
            {(updateQuote, { data }) => (
              <form
                className="form"
                id="edit-quote"
                onSubmit={e => {
                  console.log(quote.value);
                  e.preventDefault();
                  updateQuote({
                    variables: {
                      id: this.props.employee.id,
                      quote: quote.value
                    }
                  });
                  quote.value = "";
                  this.setState({ showEditQuote: false });
                  alert("Quote Updated");
                  this.props.handleClose();
                }}
              >
                <div className="form-group">
                  <label>
                    Quote:
                    <br />
                    <input
                      ref={node => {
                        quote = node;
                      }}
                      defaultValue={this.props.quote.quote}
                      autoFocus={true}
                    />
                  </label>
                </div>
                <br />


                <br />
                <br />
                <button className="button add-button" type="submit">
                  Update Quote
                </button>
              </form>
            )}
          </Mutation>
          <button
            className="button cancel-button"
            onClick={this.handleCloseEditQuote}
          >
            Cancel
          </button>
        </ReactModal>
      </div>
    );
  }
}

export default EditQuote;
