import React, { Component } from "react";
import { Mutation } from "react-apollo";
import ReactModal from "react-modal";
import gql from 'graphql-tag';

const deleteQuote = gql`
  mutation deleteQuote($id: String!) {
    removeQuote(id: $id) {
      id
      quote
    }
  }
`;

const getAll = gql`
  query {
    quotes {
      id
      quote
    }
  }
`;

ReactModal.setAppElement("#root");

class DeleteQuote extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showDeleteModal: this.props.isOpen,
      employee: this.props.deleteEmployee
    };
    this.handleOpenDeleteModal = this.handleOpenDeleteModal.bind(this);
    this.handleCloseDeleteModal = this.handleCloseDeleteModal.bind(this);
    console.log(this.state.employee);
  }

  handleOpenDeleteModal() {
    this.setState({ showDeleteModal: true });
  }

  handleCloseDeleteModal() {
    this.setState({ showDeleteModal: false });
    this.props.handleClose(false);
  }
  render() {
    return (
      <div>
        {/*Add Employee Modal */}
        <ReactModal
          name="deleteModal"
          isOpen={this.state.showDeleteModal}
          contentLabel="Delete Employee"
        >
          {/*Here we set up the mutation, since I want the data on the page to update
						after I have added someone, I need to update the cache. If not then
						I need to refresh the page to see the data updated 
						See: https://www.apollographql.com/docs/react/essentials/mutations for more
						information on Mutations
					*/}
          <Mutation
            mutation={deleteQuote}
            update={(cache, { data: { removeEmployee } }) => {
              const { employees } = cache.readQuery({
                query: getAll
              });
              cache.writeQuery({
                query: getAll,
                data: {
                  employees: employees.filter(
                    e => e.id !== this.state.employee.id
                  )
                }
              });
            }}
          >
            {(removeEmployee, { data }) => (
              <div>
                <p>
                  Are you sure you want to delete{" "}
                  {this.state.quote.quote}
                  ?
                </p>

                <form
                  className="form"
                  id="delete-employee"
                  onSubmit={e => {
                    e.preventDefault();
                    removeEmployee({
                      variables: {
                        id: this.state.employee.id
                      }
                    });
                    this.setState({ showDeleteModal: false });
                    alert("Employee Deleted");
                    this.props.handleClose();
                  }}
                >
                  <br />
                  <br />
                  <button className="button add-button" type="submit">
                    Delete Employee
                  </button>
                </form>
              </div>
            )}
          </Mutation>
          <br />
          <br />
          <button
            className="button cancel-button"
            onClick={this.handleCloseDeleteModal}
          >
            Cancel
          </button>
        </ReactModal>
      </div>
    );
  }
}

export default DeleteQuote;
